// fpsSelect jQuery plugin v1.1 http://wiki.fps2.hu/index.php/CustomSelect
(function($){$.fn.fpsSelect=function(options){var options=$.extend({selectWidth:150,selectHeight:20,containerClass:'',designClass:'',dev:false},options);return this.each(function(){var base=$(this);var base_id=base.attr('id');if(!base_id)alert('Az fpsSelect plugin csak akkor működik, ha a <select>-nek van id attribútuma!');base.wrap('<div class="'+base_id+'_container fpsContainer '+options.containerClass+'" />');base.parent().prepend('<div class="'+base_id+' fpsSelect '+options.designClass+'" />');var base_div=$('.'+base_id);base.css({position:'absolute',top:0,left:0,padding:0,margin:0,border:0,width:options.selectWidth,height:options.selectHeight,opacity:0,cursor:'pointer'}).hover(function(){base_div.addClass('hover')},function(){base_div.removeClass('hover')}).focus(function(){base_div.addClass('hover')}).focusout(function(){base_div.removeClass('hover')});refresh();base.change(function(){refresh()});$('.fpsContainer').css({position:'relative',width:options.selectWidth,height:options.selectHeight});if(options.dev){base.css({background:'red',opacity:0.5})}function refresh(){var selected_text=base.children('option[selected=selected]').html();base_div.children('.customSelect_text').remove();base_div.prepend('<span class="customSelect_text">'+selected_text+'</span>');if(options.dev)console.log(selected_text)}})}})(jQuery);